import React, { Component } from "react";
import Header from "./Components/Header/Index.js";
import Main from "./Components/Main/Index.js";
import { booksJson } from "./Services/ApiBook";
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      books: booksJson,
      genres: [],
      genresFiltered: [],
      isLoaded: false,
      booksSQL: []
    };
    this.handleDeleteBook = this.handleDeleteBook.bind(this);
    this.handleModifyBook = this.handleModifyBook.bind(this);
    this.handleNewBook = this.handleNewBook.bind(this);
    this.handleDeleteGenre = this.handleDeleteGenre.bind(this);
    this.handleAddGenre = this.handleAddGenre.bind(this);
    this.handleSelectGenre = this.handleSelectGenre.bind(this);
    this.handleDeleteAllGenre = this.handleDeleteAllGenre.bind(this);
    this.handleDeleteAllBook = this.handleDeleteAllBook.bind(this);
    this.handleDeleteGlobalGenre = this.handleDeleteGlobalGenre.bind(this);
  }

  componentDidMount() {
    this.getBooksSql();
    //this.getGenres();
    setTimeout(
      function() {
        this.setState(prevState => {
          const newState = {
            isLoaded: true
          };
          return newState;
        });
      }.bind(this),
      2000
    );
  }

  getGenres() {
    const auxArray = [];
    booksJson.map(item => {
      return item.genres.map(item => {
        if (auxArray.indexOf(item) === -1) {
          auxArray.push(item);
        }
        return auxArray;
      });
    });
    this.setState(prevState => ({
      genres: auxArray
    }));
  }
 getGenresAux(){
  const auxArray =[];
  const { booksSQL } = this.state;
  for (let index = 0; index < booksSQL.length; index++) {
    if (auxArray.indexOf(booksSQL[index].genre1) === -1 && booksSQL[index].genre1 != "undefined"){
      auxArray.push(booksSQL[index].genre1);
    }
    if (auxArray.indexOf(booksSQL[index].genre2) === -1 && booksSQL[index].genre2 != "undefined"){
      auxArray.push(booksSQL[index].genre2);
    }
    if (auxArray.indexOf(booksSQL[index].genre3) === -1 && booksSQL[index].genre3 != "undefined"){
      auxArray.push(booksSQL[index].genre3);
    }
  }
  this.setState(prevState => ({
    genres: auxArray
  }));
 }

  handleSelectGenre(e) {
    const { checked, value } = e;
    let auxArray = this.state.genresFiltered;
    if (!checked) {
      var index = auxArray.indexOf(value);
      if (index !== -1) {
        auxArray.splice(index, 1);
      }
    } else {
      auxArray.push(value);
    }
    this.setState(prevState => ({
      genresFiltered: auxArray
    }));
  }

  getArrayGenresBook(book){
    const auxArray =[];
    if (book.genre1 !== "undefined"){
      auxArray.push(book.genre1);
    }    
    if (book.genre2 !== "undefined"){
      auxArray.push(book.genre2);
    }    
    if (book.genre3 !== "undefined"){
      auxArray.push(book.genre3);
    }   
    return auxArray;
  }

  getBookList() {
    const { booksSQL, genresFiltered } = this.state;
    let auxVar = [];
    for (const book of booksSQL) {
      const genreArrayAux  = this.getArrayGenresBook(book)
      const found = genreArrayAux.some(r => genresFiltered.indexOf(r) >= 0);

      if (found === true) {
        auxVar.push(book);
      }
    }
    if (auxVar.length !== 0) {
      return auxVar;
    } else {
      return booksSQL;
    }
  }

  handleDeleteAllBook() {
    this.setState({ books: [] });
  }

  handleModifyBook1(name, bookSelected, value) {
    this.setState(prevState => {
      const newState = {
        books: prevState.books.map((book, index) => {
          if (book.id === bookSelected.id) {
            book = {
              ...book,
              [name]: value
            };
          }
          return book;
        })
      };
      return newState;
    });
  }

  handleModifyBook(newBook) {
    this.updateBookSql(newBook)
    // let newGenreArray = this.state.genres;
    // newBook.genres.map(genre => {
    //   if (this.isGenreExist(genre) === -1) {
    //     newGenreArray.push(genre);
    //   }
    //   return genre;
    // });

    // this.setState(prevState => {
    //   const newState = {
    //     books: prevState.books.map((book, index) => {
    //       if (book.id === newBook.id) {
    //         book = {
    //           id: newBook.id,
    //           genres: newBook.genres,
    //           price: newBook.price,
    //           tittle: newBook.tittle
    //         };
    //       }
    //       return book;
    //     }),
    //     genres: newGenreArray
    //   };
    //   return newState;
    // });
  }

  handleAddGenre(genre, idBook) {
    debugger;
    this.setState(prevState => {
      const newState = {
        books: prevState.books.map((book, index) => {
          if (book.id === idBook) {
            book = {
              ...book,
              genres: book.genres.concat(genre)
            };
          }
          return book;
        }),
        genres:
          this.isGenreExist(genre) === -1
            ? prevState.genres.concat(genre)
            : prevState.genres
      };
      return newState;
    });
  }

  isGenreExist(inputGenre) {
    const genres = this.state.genres;
    return genres.indexOf(inputGenre);
  }

  handleNewBook(newBook) {
    let newGenreArray = this.state.genres;
    this.addBookSql(newBook)
    this.getBooksSql();
    // newBook.genres.map(genre => {
    //   if (this.isGenreExist(genre) === -1) {
    //     newGenreArray.push(genre);
    //   }
    //   return genre;
    // });
    // newBook.id =
    //   this.state.books.length !== 0
    //     ? this.state.books[this.state.books.length - 1].id + 1
    //     : 0;
    // this.setState(prevState => ({
    //   books: prevState.books.concat(newBook),
    //   genres: newGenreArray
    // }));
  }

  handleDeleteGenre(idBook, genre) {
    this.setState(prevState => {
      const newState = {
        books: prevState.books.map((book, index) => {
          if (book.id === idBook) {
            book = {
              ...book,
              genres: genre
            };
          }
          return book;
        })
      };
      return newState;
    });
  }

  handleDeleteGlobalGenre(genreToDelete) {
    this.setState(prevState => {
      const newState = {
        books: prevState.books.map((book, index) => {
          book = {
            ...book,
            genres: book.genres.filter(function(genre) {
              return genre !== genreToDelete;
            })
          };

          return book;
        }),
        genres: prevState.genres.filter(function(genre) {
          return genre !== genreToDelete;
        })
      };
      return newState;
    });
  }

  handleDeleteAllGenre() {
    this.setState(prevState => {
      const newState = {
        books: prevState.books.map((book, index) => {
          book = {
            ...book,
            genres: []
          };

          return book;
        }),
        genres: []
      };
      return newState;
    });
  }

  handleDeleteBook(idBook) {
    this.deleteBookSql(idBook)
    this.getBooksSql();

    // this.setState(prevState => {
    //   const newState = {
    //     books: prevState.books.filter(book => book.id !== parseInt(idBook))
    //   };
    //   return newState;
    // });
  }

  getBooksSql = _ => {

    fetch('http://localhost:4000/books')
      .then(response => response.json())
      .then(response => this.setState({ booksSQL: response.data}))
      //.then(response => this.getGenres())
      .then(response => this.getGenresAux())
      .catch(err => console.error(err))
  }

  addBookSql = (newBook) => {
    fetch(`http://localhost:4000/Books/add?tittle=${newBook.tittle}&genre1=${newBook.genre1}&genre2=${newBook.genre2}&genre3=${newBook.genre3}`)
      .then(response => response.json())
      .then(this.getBooksSql)
      .catch((err => console.log(err)))
  }
  deleteBookSql = (bookId) => {
    fetch(`http://localhost:4000/Books/delete?id=${bookId}`)
      .then(response => response.json())
      .then(this.getBooksSql)
      .catch((err => console.log(err)))
  }
  updateBookSql = (bookEditData) => {
    console.log(bookEditData)
    fetch(`http://localhost:4000/Books/update?id=${bookEditData.idBooks}&tittle=${bookEditData.tittle}&genre1=${bookEditData.genre1}&genre2=${bookEditData.genre2}&genre3=${bookEditData.genre3}`)
      .then(response => response.json())
      .then(this.getBooksSql)
      .catch((err => console.log(err)))
  }

  render() {
    const { booksSQL ,genres, genresFiltered, isLoaded } = this.state;
    return (
      <div className="App">
        <Header />
        <Main
          books={this.getBookList()}
          booksSQL = {this.getBookList()}
          genres={genres}
          genresFiltered={genresFiltered}
          isLoaded={isLoaded}
          handleSelectGenre={this.handleSelectGenre}
          handleDeleteAllGenre={this.handleDeleteAllGenre}
          handleAddGenre={this.handleAddGenre}
          handleDeleteBook={this.handleDeleteBook}
          handleModifyBook={this.handleModifyBook}
          handleDeleteGenre={this.handleDeleteGenre}
          handleNewBook={this.handleNewBook}
          handleDeleteAllBook={this.handleDeleteAllBook}
          handleDeleteGlobalGenre={this.handleDeleteGlobalGenre}
        />
      </div>
    );
  }
}

export default App;
